mapper.update:
UPDATE posts
SET view_count = view_count + 1
WHERE post_id = #{postId}

UPDATE post_comments
SET content = #{content},
    created_at = NOW()
WHERE comment_id = #{commentId}
  AND user_id = #{userId}

mapper.select:
SELECT COUNT(*)
FROM post_likes
WHERE post_id = #{postId} AND user_id = #{userId}

mapper.update:
UPDATE posts
SET like_count = like_count + 1
WHERE post_id = #{postId}

UPDATE posts
SET like_count = like_count - 1
WHERE post_id = #{postId}

mapper.delete:
DELETE FROM posts
WHERE post_id = #{postId}
    AND user_id = #{userId}

mapper.update:
UPDATE reviews
SET
    rating = #{rating},
    comment = #{comment}
WHERE post_id = #{postId}
  AND plan_schedule_id = #{planScheduleId}

mapper.select:
SELECT user_id
FROM posts
WHERE post_id = #{postId}

SELECT title
FROM posts
WHERE post_id = #{postId}
